package com.basicsOfJava;

public class CancatExt {
	
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		String str1="Hello";
		String str2="World";
		
		System.out.println(a+b);
		System.out.println(a+b+str1+str2);
		System.out.println(str1+str2+a+b);
		System.out.println(str1+str2+(a+b));
	}

}
